mapper.select:
select count(*) from sys_ucenter_permission  


		and id = #{id}


		and rol_id = #{rolId}


		and fun_id = #{funId}


		and prm_params like '%'||#{prmParams}||'%'


		and prm_remark like '%'||#{prmRemark}||'%'


		and create_time  >=  str_to_date(#{createTimeBegin},  '%Y-%m-%d') 


		and create_time  <=  str_to_date(#{createTimeEnd},  '%Y-%m-%d') 


		and create_user_by = #{createUserBy}


		and update_time  >=  str_to_date(#{updateTimeBegin},  '%Y-%m-%d') 


		and update_time  <=  str_to_date(#{updateTimeEnd},  '%Y-%m-%d') 


		and update_user_by = #{updateUserBy}

mapper.delete:
delete from sys_ucenter_permission


		and id=#{id}


		and rol_id=#{rolId}


		and fun_id=#{funId}


		and prm_params=#{prmParams}


		and prm_remark=#{prmRemark}


		and create_time=#{createTime}


		and create_user_by=#{createUserBy}


		and update_time=#{updateTime}


		and update_user_by=#{updateUserBy}

mapper.select:
select id,rol_id,fun_id,prm_params,prm_remark,create_time,create_user_by,update_time,update_user_by from sys_ucenter_permission


		and id=#{id}


		and rol_id=#{rolId}


		and fun_id=#{funId}


		and prm_params=#{prmParams}


		and prm_remark=#{prmRemark}


		and create_time=#{createTime}


		and create_user_by=#{createUserBy}


		and update_time=#{updateTime}


		and update_user_by=#{updateUserBy}

select id,rol_id,fun_id,prm_params,prm_remark,create_time,create_user_by,update_time,update_user_by from sys_ucenter_permission


		and id=#{id}


		and rol_id=#{rolId}


		and fun_id=#{funId}


		and prm_params=#{prmParams}


		and prm_remark=#{prmRemark}


		and create_time=#{createTime}


		and create_user_by=#{createUserBy}


		and update_time=#{updateTime}


		and update_user_by=#{updateUserBy}


SELECT id,rol_id,fun_id,prm_params,prm_remark,create_time,create_user_by,update_time,update_user_by FROM sys_ucenter_permission 


		and id = #{id}


		and rol_id = #{rolId}


		and fun_id = #{funId}


		and prm_params like '%'||#{prmParams}||'%'


		and prm_remark like '%'||#{prmRemark}||'%'


		and create_time  >=  str_to_date(#{createTimeBegin},  '%Y-%m-%d') 


		and create_time  <=  str_to_date(#{createTimeEnd},  '%Y-%m-%d') 


		and create_user_by = #{createUserBy}


		and update_time  >=  str_to_date(#{updateTimeBegin},  '%Y-%m-%d') 


		and update_time  <=  str_to_date(#{updateTimeEnd},  '%Y-%m-%d') 


		and update_user_by = #{updateUserBy}



		ORDER BY ${sortName} ${sortOrder}
